"use strict";const EventEmitter=require("events"),driftless=require("driftless");async function sleep(ms){return new Promise(resolve=>setTimeout(resolve,ms))}class Timeout extends EventEmitter{constructor(duration){return super(),this._startedAt=null,this._duration=duration,this}start(){return this._startedAt=Date.now(),this._timeout=driftless.setDriftlessTimeout(()=>{this.emit("timeout")},this._duration),this}stop(){return driftless.clearDriftless(this._timeout),this}timedout(){return Date.now()-this._startedAt>this._duration}}module.exports={Timeout:Timeout,sleep:sleep};